import { Component } from 'react';
import './App.css';
import Logo from './Comp/Logo';
import Navi from './Comp/Navi';
import SrchBar from './Comp/SrchBar';
import Card from './Comp/Card';

let yelpAPI = require('yelp-api');
let apikey =
  'KLG6QQkitXNamc3kjNBlFWyTF0GKg-RojWfJyRYyqrRf_Uddar5TGZLbXlvqEc7YsWu7yGgYwN5_W4KUT0lNbqmg-zoN_9eRKtm4EgVNS6HJF866VH7A_U-vB9G2X3Yx';
let yelp = new yelpAPI(apikey);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      srch: '',
      cuisine: '',
      route: 'home',
      restrnts: [],
      cards: 1,
    };
  }

  onChange = (event) => {
    this.setState({ srch: event.target.value });
    console.log('this is the srch bar: ', this.state.srch);
  };
  onSelect = (event) => {
    this.setState({ cards: event.target.value });
    console.log('this is the numb. of restaurants: ', this.state.cards);
  };
  onInput = () => {
    this.setState({ cuisine: this.state.srch });
    let params = [{ categories: this.state.srch }];
    yelp
      .query('businesses/search', params)
      .then((data) => {
        console.log(data);
      })
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div className="App">
        <div>
          <Logo />
          <Navi />
        </div>
        <h1> Yelp</h1>

        <SrchBar
          select={this.onSelect}
          change={this.onChange}
          input={this.onInput}
          val={this.state.cards}
        />
        <div
          style={{ display: 'flex', justifyContent: 'space-around' }}
          className="pa3 ma2"
        >
          <Card />
          <Card />
          <Card />
        </div>
      </div>
    );
  }
}

export default App;
