import { Component } from 'react';
import './App.css';
import Logo from './Comp/Logo';
import Navi from './Comp/Navi';
import SrchBar from './Comp/SrchBar';
import Card from './Comp/Card';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      srch: '',
      cuisine: '',
      route: 'home',
      res: [],
    };
  }

  onChange = (event) => {
    this.setState({ srch: event.target.value });
    console.log('this is the srch bar: ', this.state.srch);
  };
  onInput = () => {
    this.setState({ cuisine: this.state.srch });
    console.log('this is the cuisine: ', this.state.srch);
  };

  render() {
    return (
      <div className="App">
        <div>
          <Logo />
          <Navi />
        </div>
        <h1> Yelp</h1>

        <SrchBar change={this.onChange} input={this.onInput} />
        <div className="pa0">
          <Card />
        </div>
      </div>
    );
  }
}

export default App;
