import { Component } from 'react';
import './App.css';
import Logo from './Comp/Logo';
import Navi from './Comp/Navi';
import SrchBar from './Comp/SrchBar';
import Card from './Comp/Card';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      srch: '',
      cuisine: '',
      route: 'home',
      restrnts: [],
      cards: 1,
    };
  }

  onChange = (event) => {
    this.setState({ srch: event.target.value });
    console.log('this is the srch bar: ', this.state.srch);
  };
  onSelect = (event) => {
    this.setState({ cards: event.target.value });
    console.log('this is the numb. of restaurants: ', this.state.cards);
  };
  onInput = () => {
    this.setState({ cuisine: this.state.srch });
    console.log('this is the cuisine: ', this.state.srch);
  };

  render() {
    return (
      <div className="App">
        <div>
          <Logo />
          <Navi />
        </div>
        <h1> Yelp</h1>

        <SrchBar
          select={this.onSelect}
          change={this.onChange}
          input={this.onInput}
          val={this.state.cards}
        />
        <div
          style={{ display: 'flex', justifyContent: 'space-around' }}
          className="pa3 ma2"
        >
          <Card />
          <Card />
          <Card />
        </div>
      </div>
    );
  }
}

export default App;
