import { Component } from 'react';
import './App.css';
import Logo from './Comp/Logo';
import Navi from './Comp/Navi';
import SrchBar from './Comp/SrchBar';
import Card from './Comp/Card';
import axios from 'axios';

let apikey =
  'KLG6QQkitXNamc3kjNBlFWyTF0GKg-RojWfJyRYyqrRf_Uddar5TGZLbXlvqEc7YsWu7yGgYwN5_W4KUT0lNbqmg-zoN_9eRKtm4EgVNS6HJF866VH7A_U-vB9G2X3Yx';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: '',
      srch: '',
      cuisine: '',
      route: 'home',
      rest: [],
      cards: 1,
    };
  }

  onChange = (event) => {
    this.setState({ srch: event.target.value });
  };
  onSelect = (event) => {
    this.setState({ cards: event.target.value });
  };
  onInput = () => {
    this.setState({ cuisine: this.state.srch });

    axios
      .get(
        `${'https://cors-anywhere.herokuapp.com/'}https://api.yelp.com/v3/businesses/search?location=southgate`,
        {
          headers: {
            Authorization: `Bearer ${apikey}`,
          },
          params: {
            categories: this.state.cuisine,
          },
        }
      )
      .then((res) => {
        this.setState({ rest: res.data.businesses.slice(0, this.state.cards) });
        console.log(
          'this is what was sliced:',
          res.data.businesses.slice(0, this.state.cards)
        );
      })
      .catch((err) => {
        console.log('error');
      });
  };

  render() {
    return (
      <div className="App">
        <div>
          <Logo />
          <Navi />
        </div>
        <h1> Yelp</h1>

        <SrchBar
          select={this.onSelect}
          change={this.onChange}
          input={this.onInput}
          val={this.state.cards}
        />
        <div
          style={{ display: 'flex', justifyContent: 'space-around' }}
          className="pa3 ma2"
        >
          <Card
            name={this.state.rest[0].name}
            pic={this.state.rest[0].image_url}
            rating={this.state.rest[0].rating}
            add={this.state.rest[0].location}
          />
          ;
          {/* {this.state.rest.map((rst) => {
            <Card
              name={rst.name}
              pic={rst.image_url}
              rating={rst.rating}
              add={rst.location}
            />;
          })} */}
        </div>
      </div>
    );
  }
}

export default App;
